using UnityEngine;using UnityEditor;public class Hoe : MonoBehaviour{    public Vector3 init_position;    // Use this for initialization    new Collider2D collider;    void Start()    {        collider = GetComponent<Collider2D>();        init_position = transform.position;    }    // Update is called once per frame    void Update()    {        // check for click        if (Input.GetMouseButtonDown(0) && collider.OverlapPoint(Camera.main.ScreenToWorldPoint(Input.mousePosition)))        {            Controls.mode = Controls.Mode.tilling;        }        Vector2 touchPos = Camera.main.ScreenToWorldPoint(Input.mousePosition);        if (Input.GetMouseButton(0))        {            transform.position = new Vector3(touchPos.x, touchPos.y, 0.1f);          }        else        {            transform.position = Vector3.Lerp(transform.position, init_position, 0.1f);        }    }}