using UnityEngine;using UnityEditor;public class Hoe : MonoBehaviour{    new Collider2D collider;    void Start()    {        collider = GetComponent<Collider2D>();    }    // Update is called once per frame    void Update()    {        // check for click        if (Input.GetMouseButtonDown(0) && collider.OverlapPoint(Camera.main.ScreenToWorldPoint(Input.mousePosition)))        {            Controls.mode = Controls.Mode.tilling;        }    }}