using System.Collections;using System.Collections.Generic;using UnityEngine;public class SeedPile : MonoBehaviour{    public GameObject seed;    public Sprite[] sprites;    int _quantity;    public int quantity    {        get { return _quantity; }        set        {            _quantity = value;            if (_quantity <= 3)            {                renderer.sprite = sprites[_quantity];            }            else            {                renderer.sprite = sprites[3];            }        }    }    new Collider2D collider;    new SpriteRenderer renderer;    // Use this for initialization    void Start ()    {        collider = GetComponent<Collider2D>();        renderer = GetComponent<SpriteRenderer>();    }		// Update is called once per frame	void Update ()    {        foreach (Touch touch in Controls.GetTouchesAndMouse())        {            if (touch.phase == TouchPhase.Began && collider.OverlapPoint(Camera.main.ScreenToWorldPoint(touch.position)))            {                if (quantity > 0)                {                    Controls.mode = Controls.Mode.planting;                    Instantiate(seed, transform.position, Quaternion.identity).GetComponent<SeedPlanting>().enabled = true;                    quantity--;                }            }        }    }}